sg.theme('DarkGrey11')

def get_stock(numStock = 1):
    # valueData = data.set_index('Ticker', drop = True)

    if numStock > 1:
        stock = [choices(list(data.keys()), [data[c][1] for c in list(data.keys())])[0] for i in range(numStock)]
        # stock = [choices(data['Ticker'], data['Prob'])[0] for i in range(numStock)]
        value = [data[st][0] for st in stock]
        # value = valueData.loc[stock, 'Value']
    else:
        stock = [choices(list(data.keys()), [data[c][1] for c in list(data.keys())])[0] for i in range(numStock)]
        # stock = choices(data['Ticker'], data['Prob'])
        value = [data[stock[0]][0]]
        # value = valueData.loc[stock, 'Value']

    return stock, value

if __name__ == '__main__':
    layout = [ [sg.Text('Enter number of stocks', justification = 'center', size = (180, 2))],
               [sg.Input(key = 'stockNum', size = (180, 3))],
               [sg.Text(' ', size = (180, 2), key = 'text')],
                [sg.Submit('Buy for $200 each', button_color='green', size = (180, 3))]]
    window = sg.Window(title = 'Mystery Stock', layout=layout, grab_anywhere = True, size = [400, 200])

    while True:
        event, values = window.read()
        if event == 'Buy for $200 each':
            stockNum = int(values['stockNum'])
            if stockNum > 20:
                window['text'].update('Maximum number of stocks for single purchase is 20')
            else:
                stock, value = get_stock(stockNum)
                popupStr = 'YOU RECEIVED:\n\n' \
                           'Stock   ||   Value \n\n'
                for i in range(len(stock)):
                    popupStr = popupStr + stock[i] + ' '*(10-len(stock[i])) + '$' + str(round(value[i], 2))  + '\n'
                popupStr = popupStr + '\nTOTAL: '+ ' '*2 + '$' + str(round(sum(value), 2))
                sg.Popup(popupStr, title = 'Result')
        if event in (None, 'Exit') or event == sg.WIN_CLOSED:
            break

    window.close()


